import React from "react";
import {useState} from "react"
import {useEffect} from "react"

//Table rows elementu template

function createRow(input){

  let handleClick =  async (input) => {
    console.log(input)
    try {
      let res = await fetch("http://localhost:3001/form", {
        method: "POST",
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({input}), 
      });
      let resJson = await res;
      
    } catch (err) {
      
    }

  }

  
  return (
    <tr key = {input.ak} onClick={() => handleClick(input.ak)}  className="clickable" >          
    <td>{input.ak}</td>
    <td>{input.fname}</td>
    <td>{input.lname}</td>      
   </tr>
  );
}

//Fetchina json ir sukuria lentele

function TableData(){
  const [data, setData] = useState([]);
  useEffect(() => {
      async function fetchMyApi() {
        let res = await fetch("http://localhost:3001/api")
        res = await res.json()
        setData(res)
      }
      fetchMyApi()
    }, []);
  
    return (
       data.map(createRow)
    );
}


export default TableData;
