import React from "react";
import {useState} from "react"
import {useEffect} from "react"

let detailsData = [];
//Table rows elementu template

function createRow(input){

  let handleClick =  async (input) => {
    try {
      let res = await fetch("http://localhost:3001/details", {
        method: "POST",
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({input}), 
      });
      detailsData = await res.json()
      console.log(detailsData);
      
    } catch (err) {
      
    }

  }

  
  return (
    <tr key = {input.ak} onClick={() => handleClick(input.ak)}  className="clickable" >          
    <td>{input.ak}</td>
    <td>{input.fname}</td>
    <td>{input.lname}</td>      
   </tr>
  );
}


function Details() {
  const [data, setData] = useState(detailsData);
  
    return (
      <div>
        <p>qq {detailsData.fname}</p>
        <p>qq {detailsData.lname}</p>
        <p>qq {detailsData.ak}</p> 
        <p>qq {detailsData.email}</p>
        <p>qq {detailsData.age}</p>
      </div>
    )
  }
//Fetchina json ir sukuria lentele

function TableData(){
  const [data, setData] = useState([]);
  useEffect(() => {
      async function fetchMyApi() {
        let res = await fetch("http://localhost:3001/api")
        res = await res.json()
        setData(res)
      }
      fetchMyApi()
    }, []);
  
    return (
       data.map(createRow)
    );
}




export default TableData;
export {detailsData};
