import React from 'react'
import { useState } from "react";

function Form() {

    const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [message, setMessage] = useState("");

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://httpbin.org/post", {
        method: "POST",
        body: JSON.stringify({
          name: name,
          email: email,
          mobileNumber: mobileNumber,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setName("");
        setEmail("");
        setMobileNumber("");
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };





  return (
    <div>
      <form class="" action="/form" method="post">
      <p>Vardas</p><input type="text" name="fname" required placeholder="Vardas" /> <br />
      <p>Pavardė</p><input type="text" name="lname" required placeholder="Pavardė" /> <br />
      <p>Asmens Kodas</p><input type="tel" minlength="11" name="ak" required placeholder="Asmens kodas" /> <br />
      <p>E-mail</p><input type="email" name="email" required placeholder="E-mail" /> <br />
      <p>Amžius</p><input type="number" name="age" required placeholder="Amžius" /> <br />
      <button class="submitButton" type="submit" name="submit">Pateikti</button>
    </form>
    </div>
  )
}

export default Form
